INSERT INTO `autobroadcast` (`realmid`, `id`, `weight`, `text`) VALUES (-1, 1, 1, 'Thank you for choosing to play on Dalaran-WoW, we really appreciate your choice. If you have any questions please use our support and suggestions section on the forum.');
INSERT INTO `autobroadcast` (`realmid`, `id`, `weight`, `text`) VALUES (-1, 2, 1, 'You can write /join Global and chat with other members from your faction. Respect other players as you would like to be respected. Any sort of harassment or bashing will be warned, if continued shall lead to a ban.');
INSERT INTO `autobroadcast` (`realmid`, `id`, `weight`, `text`) VALUES (-1, 3, 1, 'To maintain the server and help our community, you can donate to our server to keep it in order and alive. Dalaran-WoW is thankful for all the in-coming support and donations');
INSERT INTO `autobroadcast` (`realmid`, `id`, `weight`, `text`) VALUES (-1, 4, 1, 'Remember, you can vote for the server every 12 hours through our website ([www.dalaran-wow.com]). If you vote, you\'re helping us to grow and to spread - for that you are going to be rewarded with vote points which you can exchange using our Vote Shop.');
INSERT INTO `autobroadcast` (`realmid`, `id`, `weight`, `text`) VALUES (-1, 5, 1, 'Before you attempt to use the in-game ticket system, take a look at the rules and guidelines on our Forums. We\'ll start to close the tickets that do not contain enough information, if you specify your issue, we\'ll be able to help you out faster and better.');
