INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `comment`) VALUES
(35004, 0, 0, "The Sunreavers are proud to present their representatives in this trial by combat.", 14, 0, 0, 2, 0, 0, "ToC5 - Horde Intro 1"),
(35005, 0, 0, "The Silver Covenant is pleased to present their contenders for this event, Highlord.", 14, 0, 0, 2, 0, 0, "ToC5 - Alliance Intro 1"),
(34996, 17, 0, "Welcome, champions. Today, before the eyes of your leaders and peers, you will prove youselves worthy combatants.", 14, 0, 0, 2, 0, 0, "ToC5 - Intro 2"),
