INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `comment`) VALUES
(381535, 0, 0, "Welcome, champions. Today, before the eyes of your leaders and peers, you will prove youselves worthy combatants.", 14, 0, 0, 2, 0, 0, "ToC5 - Intro 2"),
(381535, 1, 0, "Fight well, Horde! Lok''tar Ogar!", 14, 0, 0, 2, 0, 0, "ToC - Horde Intro 3"),
(381535, 2, 0, "I have no taste for these games, Tirion.  Still... I trust they will perform admirably.", 12, 0, 0, 2, 0, 0, "ToC - Alliance Intro 3"),
(381535, 3, 0, "Finally, a fight worth watching.", 14, 0, 0, 2, 0, 0, "ToC - Horde Intro 4"),
(381535, 4, 0, "Of course they will.", 12, 0, 0, 2, 0, 0, "ToC - Alliance Intro 4"),
(381535, 5, 0, "I did not come here to watch animals tear at each other senselessly, Tirion.", 12, 0, 0, 2, 0, 0, "ToC - Horde Intro 5"),
(381535, 6, 0, "Admirably? Hah!  I will enjoy watching your weak little champions fail, human.", 14, 0, 0, 2, 0, 0, "ToC - Alliance Intro 5"),
(381535, 7, 0, "They're worthy fighters, you'll see.", 12, 0, 0, 2, 0, 0, "ToC - Horde Intro 6"),
(381535, 8, 0, "Garrosh, enough.", 12, 0, 0, 2, 0, 0, "ToC - Alliance Intro 6"),
(381535, 9, 0, "You will first be facing three of the Grand Champions of the Tournament! These fierce contenders have beaten out all others to reach the pinnacle of skill in the joust.", 14, 0, 0, 2, 0, 0, "ToC5 - Intro 7");
