INSERT INTO creature_text VALUES(25729, 0, 0, "This is it, $N. The start of the end of the world. And you're here to see it off...", 12, 0, 100, 0, 0, 0, 0, 0,'Shadowstalker Getry');
INSERT INTO creature_text VALUES(25729, 1, 0, "Let's go...", 12, 0, 100, 0, 0, 0, 0, 0,'Shadowstalker Getry');
INSERT INTO creature_text VALUES(25729, 2, 0, "I... I can't believe it... Saurfang... I... I am honored... honored to have fought along side you, sir.", 12, 0, 100, 1, 0, 0, 0, 0,'Shadowstalker Getry');
INSERT INTO creature_text VALUES(25729, 3, 0, "You should return to Warsong Hold now, friend. Hellscream will surely want to hear of what just happened here... of Saurfang...", 12, 0, 100, 1, 0, 0, 0, 0,'Shadowstalker Getry');
INSERT INTO creature_text VALUES(25729, 4, 0, "Farewell...", 12, 0, 100, 0, 0, 0, 0, 0,'Shadowstalker Getry');
INSERT INTO creature_text VALUES(25618, 0, 0, "Is this it? Is this all the mighty Horde could muster?", 12, 0, 100, 0, 0, 0, 0, 0,'Varidus the Flenser');
INSERT INTO creature_text VALUES(25618, 1, 0, "Pathetic.", 12, 0, 100, 11, 0, 0, 0, 0,'Varidus the Flenser');
INSERT INTO creature_text VALUES(25618, 2, 0, "I've never understood your type. The hero...", 12, 0, 100, 0, 0, 0, 0, 0,'Varidus the Flenser');
INSERT INTO creature_text VALUES(25618, 3, 0, "Why won't you just let go? Why do you fight the inevitable?", 12, 0, 100, 1, 0, 0, 0, 0,'Varidus the Flenser');
INSERT INTO creature_text VALUES(25618, 4, 0, "The Lich King cannot be stopped. Accept it.", 12, 0, 100, 274, 0, 0, 0, 0,'Varidus the Flenser');
INSERT INTO creature_text VALUES(25618, 5, 0, "This world is coming to an end. Let. It. Burn.", 12, 0, 100, 25, 0, 0, 0, 0,'Varidus the Flenser');
INSERT INTO creature_text VALUES(25618, 6, 0, "Take him away and prepare him for reanimation.", 12, 0, 100, 1, 0, 0, 0, 0,'Varidus the Flenser');
INSERT INTO creature_text VALUES(25618, 7, 0, "What's this now?", 12, 0, 100, 1, 0, 0, 0, 0,'Varidus the Flenser');
INSERT INTO creature_text VALUES(25618, 8, 0, "Then you are a fool.", 12, 0, 100, 1, 0, 0, 0, 0,'Varidus the Flenser');
INSERT INTO creature_text VALUES(25618, 9, 0, "You? A lone orc? Against me and...", 12, 0, 100, 1, 0, 0, 0, 0,'Varidus the Flenser');
INSERT INTO creature_text VALUES(25618, 10, 0, "RISE!", 14, 0, 100, 0, 0, 0, 0, 0,'Varidus the Flenser');
INSERT INTO creature_text VALUES(25751, 0, 0, "I'll rip out your shriveled heart with my bare hands before any harm comes to $N, Necromancer.", 12, 0, 100, 1, 0, 0, 0, 0,'High Overlord Saurfang');
INSERT INTO creature_text VALUES(25751, 1, 0, "You were never alone, $N.", 12, 0, 100, 0, 0, 0, 0, 0,'High Overlord Saurfang');
INSERT INTO creature_text VALUES(25751, 2, 0, "This world that you seek to destroy is our home.", 12, 0, 100, 1, 0, 0, 0, 0,'High Overlord Saurfang');
INSERT INTO creature_text VALUES(25751, 3, 0, "We will fight you with every fiber of our being- Until we are nothing more than dust and debris. We will fight until the end.", 12, 0, 100, 5, 0, 0, 0, 0,'High Overlord Saurfang');
INSERT INTO creature_text VALUES(25751, 4, 0, "A fool that is about to end you, Necrolord. There will be nothing left of you for the Lich King to reanimate!", 12, 0, 100, 1, 0, 0, 0, 0,'High Overlord Saurfang');
INSERT INTO creature_text VALUES(25751, 5, 0, "You'll make no mention of me. Either of you!", 12, 0, 100, 1, 0, 0, 0, 0,'High Overlord Saurfang');
